// PRODUCTION-READY NOTION CONTENT EXTRACTION (V2 - FIXED OUTPUT)

try {
  console.log('üîç Starting comprehensive content extraction...');

  const extractItems = $items('Notion ‚Äì Extract All Blocks');
  if (!extractItems?.length) {
    throw new Error('No blocks from Notion ‚Äì Extract All Blocks');
  }
  console.log(`üì• Processing ${extractItems.length} block items`);

  const allBlocks = extractItems.map(item => item.json);
  const blockMap = new Map();
  const topLevelBlocks = [];

  // Build hierarchical tree
  allBlocks.forEach(block => {
    blockMap.set(block.id, { ...block, children: [] });
  });

  allBlocks.forEach(block => {
    if (block.parent?.type === 'page_id') {
      topLevelBlocks.push(blockMap.get(block.id));
    } else if (block.parent?.type === 'block_id') {
      const parent = blockMap.get(block.parent.block_id);
      if (parent) {
        parent.children.push(blockMap.get(block.id));
      }
    }
  });

  console.log(`üå≥ Built tree with ${topLevelBlocks.length} top-level blocks`);

  // Text extraction helper
  const extractText = (richTextArray) => {
    if (!Array.isArray(richTextArray)) return '';
    return richTextArray
      .map(item => item.plain_text || item.text?.content || '')
      .join('')
      .trim();
  };

  // Image processing helper
  const processImage = (url, caption = '') => {
    if (!url) return null;
    try {
      return {
        url: url,
        caption: caption,
        alt_text: caption || 'Content image',
        processing_needed: true
      };
    } catch {
      return null;
    }
  };

  // Recursive block renderer
  function renderBlock(block, level = 0) {
    if (!block?.type) return { text: '', sections: [], images: [] };

    const indent = '  '.repeat(level);
    const blockData = block[block.type] || {};
    let content = '';
    let sections = [];
    let images = [];
    let text = extractText(blockData?.rich_text || blockData?.text || []);

    switch (block.type) {
      case 'heading_1':
        content = `\\n# ${text}\\n\\n`;
        if (text) sections.push({ level: 1, title: text, content: '', id: block.id });
        break;
      case 'heading_2':
        content = `\\n## ${text}\\n\\n`;
        if (text) sections.push({ level: 2, title: text, content: '', id: block.id });
        break;
      case 'heading_3':
        content = `\\n### ${text}\\n\\n`;
        if (text) sections.push({ level: 3, title: text, content: '', id: block.id });
        break;
      case 'paragraph':
        if (text) content = `${text}\\n\\n`;
        break;
      case 'bulleted_list_item':
        if (text) content = `${indent}- ${text}\\n`;
        break;
      case 'numbered_list_item':
        if (text) content = `${indent}1. ${text}\\n`;
        break;
      case 'toggle':
        if (text) {
          content = `\\n‚ñ∂Ô∏è ${text}\\n`;
          sections.push({ level: 4, title: text, content: '', id: block.id, type: 'toggle' });
        }
        break;
      case 'callout':
        const icon = blockData?.icon?.emoji || 'üí°';
        if (text) content = `\\n${icon} **${text}**\\n\\n`;
        break;
      case 'quote':
        if (text) content = `\\n> ${text}\\n\\n`;
        break;
      case 'code':
        const language = blockData?.language || 'text';
        if (text) {
          content = `\\n\`\`\`${language}\\n${text}\\n\`\`\`\\n\\n`;
          sections.push({ level: 5, title: `Code: ${language}`, content: text, id: block.id, type: 'code' });
        }
        break;
      case 'divider':
        content = '\\n---\\n\\n';
        break;
      case 'image':
        const imageUrl = blockData?.file?.url || blockData?.external?.url;
        if (imageUrl) {
          const processedImage = processImage(imageUrl, text);
          if (processedImage) {
            images.push(processedImage);
            content = `\\n[üì∏ Image: ${text || 'Visual content'}]\\n\\n`;
          }
        }
        break;
      case 'video':
        content = `\\n[üé• Video: ${text || 'Video content'}]\\n\\n`;
        break;
      case 'bookmark':
        const bookmarkUrl = blockData?.url || '';
        if (bookmarkUrl) content = `\\n[üîó ${text || bookmarkUrl}](${bookmarkUrl})\\n\\n`;
        break;
      default:
        if (text) content = `${indent}${text}\\n\\n`;
        break;
    }

    // Process children recursively
    if (block.children?.length) {
      const childrenResult = block.children
        .map(child => renderBlock(child, level + 1))
        .reduce((acc, result) => {
          acc.text += result.text;
          acc.sections = acc.sections.concat(result.sections);
          acc.images = acc.images.concat(result.images);
          return acc;
        }, { text: '', sections: [], images: [] });

      content += childrenResult.text;
      sections = sections.concat(childrenResult.sections);
      images = images.concat(childrenResult.images);
    }

    return { text: content, sections: sections, images: images };
  }

  // Process all blocks
  const result = topLevelBlocks
    .map(block => renderBlock(block))
    .reduce((acc, blockResult) => {
      acc.text += blockResult.text;
      acc.sections = acc.sections.concat(blockResult.sections);
      acc.images = acc.images.concat(blockResult.images);
      return acc;
    }, { text: '', sections: [], images: [] });

  // Clean up text
  let fullText = result.text
    .replace(/\n{3,}/g, '\n\n')
    .replace(/\s+$/gm, '')
    .trim();

  const stats = {
    totalBlocks: allBlocks.length,
    processedBlocks: topLevelBlocks.length,
    characterCount: fullText.length,
    wordCount: fullText.split(/\s+/).filter(w => w.length > 0).length,
    sections: result.sections.length,
    images: result.images.length,
    toggleSections: result.sections.filter(s => s.type === 'toggle').length,
    codeSections: result.sections.filter(s => s.type === 'code').length
  };

  console.log('‚úÖ Content extraction complete:', stats);

  // ‚≠ê KEY FIX: Wrap everything in 'sourceContent'
  return [{
    json: {
      sourceContent: {
        name: $('Notion ‚Äì Update to Processing').first().json.name,
        categories: $('Notion ‚Äì Update to Processing').first().json.property_category,
        fullText: fullText,
        sections: result.sections,
        images: result.images,
        extractionStats: stats,
        contentMetadata: {
          totalSections: result.sections.length,
          hasImages: result.images.length > 0,
          hasCode: result.sections.some(s => s.type === 'code'),
          hasToggles: result.sections.some(s => s.type === 'toggle'),
          hasLists: fullText.includes('- ') || /^\d+\.\s/m.test(fullText),
          complexity: stats.wordCount > 800 ? 'high' : stats.wordCount > 400 ? 'medium' : 'low'
        }
      }
    }
  }];

} catch (error) {
  console.error('‚ùå Content extraction failed:', error.message);
  return [{
    json: {
      sourceContent: {
        fullText: `Content extraction error: ${error.message}`,
        sections: [],
        images: [],
        extractionStats: { error: true },
        contentMetadata: { error: error.message }
      }
    }
  }];
}

