// Main n8n code node logic
const incomingItems = $input.all();


// INTELLIGENT CONTENT SUMMARIZATION (Cost-Optimized)
// Limits to 2000 chars while preserving structure
// ────────────────────────────────────────────────────────────────
function intelligentSummarize(sourceContent) {
  if (!sourceContent) return { summary: "No content available", structure: "empty", wordCount: 0,complexity: "unknown" };

  // Extract key structural elements
  const { fullText, sections, extractionStats } = sourceContent;

  let summary = "";
  // Priority 1: Section headings (highest signal-to-noise ratio)
  const sectionTitles = (sections || [])
    .filter(s => s.title && s.title.length > 3) // Skip very short titles
    .map(s => `• ${s.title}`)
    .slice(0, 12) // Max 12 headings
    .join('\n');

  if (sectionTitles) {
    summary += "**Key Sections:**\n" + sectionTitles + "\n\n";
  }

  // Priority 2: First substantive section content (provides context)
  if (sections && sections.length > 0) {
    // Find first section with actual content (not just heading)
    const contentSection = sections.find(s => 
      s.content && s.content.length > 50
    );

    if (contentSection) {
      summary += "**Core Content Sample:**\n" 
        + contentSection.content.substring(0, 500)
        .replace(/\n+/g, ' ') // Remove line breaks for compactness
        + "...\n";
    }
  }

  // Priority 3: Fallback to fullText if sections are empty
  if (!sections || sections.length === 0) {
    summary = "**Content Preview:**\n" + (fullText || "").substring(0, 1800) + "...";
  }

  // Enforce strict 2000 char limit (API cost control)
  summary = summary.substring(0, 2000);

  return {
    summary,
    structure: extractionStats?.hasToggles ? "hierarchical" : "linear",
    wordCount: extractionStats?.wordCount || 0,
    complexity: extractionStats?.complexity || "unknown",
    sectionCount: sections?.length || 0,
    hasCode: extractionStats?.codeSections > 0,
    hasToggles: extractionStats?.toggleSections > 0
  };
}

// ────────────────────────────────────────────────────────────────
// PROCESS AND MERGE
// ────────────────────────────────────────────────────────────────
return incomingItems.map(item => {
  const sourceContent = item.json.sourceContent || {};
  const contentSummary = intelligentSummarize(sourceContent);

  return {
    json: {
      // Preserve all original data from previous nodes
      ...item.json,
      // Added personal context
      personalContext,
      // Add optimized content summary for AI prompts
      contentSummary, // This is what AI nodes will use
      // Keep full sourceContent for reference if needed
      _fullSourceContent: sourceContent
    }
  };
});

// INTELLIGENT NOTION CONTENT SUMMARIZATION (Cost-Optimized)
// Limits to 2000 chars while preserving structure
// ────────────────────────────────────────────────────────────────
function intelligentSummarize(sourceContent) {
  if (!sourceContent) return { summary: "", structure: "empty" };

  // Extract key structural elements
  const { fullText, sections, extractionStats } = sourceContent;

  // Priority 1: Section headings (most informative)
  let summary = "";
  const sectionTitles = (sections || [])
    .filter(s => s.title && s.title.length > 0)
    .map(s => `• ${s.title}`)
    .slice(0, 10) // Max 10 headings
    .join('\n');

  if (sectionTitles) {
    summary += "**Key Sections:**\n" + sectionTitles + "\n\n";
  }

  // Priority 2: First substantive content (context)
  if (sections && sections.length > 0) {
    const firstContentSection = sections.find(s => s.content && s.content.length > 100);
    if (firstContentSection) {
      summary += "**Core Content:**\n" + firstContentSection.content.substring(0, 600) + "...\n";
    }
  }

  // Priority 3: Full text truncation if sections unavailable
  if (!sections || sections.length === 0) {
    summary = "**Content Preview:**\n" + (fullText || "").substring(0, 1500) + "...";
  }

  // Enforce 2000 char limit
  summary = summary.substring(0, 2000);

  return {
    summary,
    structure: extractionStats?.hasToggles ? "hierarchical" : "linear",
    wordCount: extractionStats?.wordCount || 0,
    complexity: extractionStats?.complexity || "unknown"
  };
}

// PROCESS AND MERGE
return incomingItems.map(item => {
  const sourceContent = item.json.sourceContent || {};
  const contentSummary = intelligentSummarize(sourceContent);

  return {
    json: {
      ...item.json,
      personalContext,
      contentSummary, // This is what AI nodes will use
      // Keep full sourceContent for reference
      _fullSourceContent: sourceContent
    }
  };
});

// ═══════════════════════════════════════════════════════════════
// FINAL CONTEXT MERGER (V3.1 - FULL TEXT)
// This version works with a single, pre-merged input item and sends the full text.
// ═══════════════════════════════════════════════════════════════

console.log('🔗 Starting final context merger...');

const mergedItem = $json;

if (!mergedItem) {
  throw new Error('❌ No merged item received from the previous node.');
}

console.log('✅ Received combined data object.');

// ────────────────────────────────────────────────────────────────
// EXTRACT CONTEXT DATA
// ────────────────────────────────────────────────────────────────
const personalContext = mergedItem.personalContext;
const sourceContent = mergedItem.sourceContent;
const contentSummary = mergedItem.contentSummary;
const perplexityChoices = mergedItem.choices;

if (!personalContext || !sourceContent) {
  throw new Error('❌ Critical data (personalContext or sourceContent) is missing.');
}

console.log(`👤 Personal context for: ${personalContext.name}`);
console.log(`📄 Source content title: ${sourceContent.name || 'Untitled'}`);

// ────────────────────────────────────────────────────────────────
// PARSE PERPLEXITY RESEARCH DATA (with robust fallbacks)
// ────────────────────────────────────────────────────────────────
let research = {};

try {
  if (!perplexityChoices || perplexityChoices.length === 0) {
    throw new Error('No "choices" array found.');
  }
  let content = perplexityChoices[0].message?.content || '';
  if (!content) {
    throw new Error('Perplexity message content is empty.');
  }
  console.log('🔍 Found Perplexity data. Attempting to parse...');
  content = content.replace(/```json\n?|```/g, '').trim();
  const parsedResearch = JSON.parse(content);
  research = parsedResearch;
  console.log('✅ Perplexity JSON parsed successfully.');
} catch (error) {
  console.warn(`⚠️ Perplexity parsing failed: ${error.message}. Using fallbacks.`);
  const primaryCategory = sourceContent?.categories?.[0]?.replace(/\s/g, '') || 'Tech';
  research = {
    trendingHashtags: {
      twitter: ['#BuildInPublic', `#${primaryCategory}`, '#Automation', '#NoCode', '#n8n'],
      linkedin: ['#ProcessAutomation', `#${primaryCategory}`, '#SystemsThinking', '#AI'],
    },
    // ... other fallback data
  };
}

// ────────────────────────────────────────────────────────────────
// BUILD THE FINAL MASTER CONTEXT OBJECT
// ────────────────────────────────────────────────────────────────
const masterContext = {
  personalContext: personalContext,
  sourceContent: {
    title: sourceContent.name || 'Untitled Content',
    categories: sourceContent.categories || ['General'],
    primaryCategory: sourceContent.categories?.[0] || 'General',
    summary: contentSummary?.summary || 'No summary available.',
    wordCount: contentSummary?.wordCount || 0,
    structure: contentSummary?.structure || 'linear',
    complexity: contentSummary?.complexity || 'unknown',
    // ⭐ KEY CHANGE IS HERE: Pass the entire text, not a preview
    fullText: sourceContent.fullText || '',
  },
  research: research,
  originalId: mergedItem.originalId,
  sessionId: mergedItem.sessionId,
  workflowMetadata: {
    notionPageId: sourceContent.id || null,
    extractionStats: sourceContent.extractionStats || {},
    hasImages: sourceContent.images?.length > 0 || false,
    processingTime: new Date().toISOString(),
  }
};

console.log('✅ Master context object created successfully!');
return [{ json: masterContext }];

