// ═══════════════════════════════════════════════════════════════
// UNIFIED: PARSE GEMINI STRATEGY + MERGE WITH CONTEXT DATA
// Handles Gemini's nested response structure + fixes null strings
// ═══════════════════════════════════════════════════════════════

console.log('🔧 Starting unified strategy parsing + merge...');

const geminiRawOutput = $input.first().json;
const contextData = $('Code – CONTEXT MERGER').first().json;

// ────────────────────────────────────────────────────────────────
// STEP 1: EXTRACT STRATEGY FROM GEMINI'S NESTED STRUCTURE
// ────────────────────────────────────────────────────────────────
let strategy = null;

try {
  // Gemini wraps response in: content.parts[0].text (as JSON string)
  const geminiTextContent = geminiRawOutput.content?.parts?.[0]?.text || 
                            geminiRawOutput.parts?.[0]?.text || 
                            geminiRawOutput.text ||
                            JSON.stringify(geminiRawOutput);

  console.log('📝 Gemini raw text length:', geminiTextContent.length);

  // Parse the JSON string
  const parsedStrategy = JSON.parse(geminiTextContent);

  // Extract content_strategy (handle both nested and flat)
  strategy = parsedStrategy.content_strategy || parsedStrategy;

  console.log('✅ Strategy parsed successfully');

} catch (error) {
  console.error('❌ Strategy parsing failed:', error.message);
  throw new Error(`Failed to parse Gemini strategy: ${error.message}`);
}

// ────────────────────────────────────────────────────────────────
// STEP 2: FIX NULL STRINGS (Gemini returns "null" instead of null)
// ────────────────────────────────────────────────────────────────
function fixNullStrings(obj) {
  if (typeof obj !== 'object' || obj === null) return obj;

  for (let key in obj) {
    if (obj[key] === "null" || obj[key] === "None" || obj[key] === "false") {
      obj[key] = obj[key] === "false" ? false : null;
    } else if (typeof obj[key] === 'object') {
      fixNullStrings(obj[key]);
    }
  }
  return obj;
}

strategy = fixNullStrings(strategy);
console.log('🔧 Null strings converted to proper null values');

// ────────────────────────────────────────────────────────────────
// STEP 3: VALIDATE STRATEGY STRUCTURE
// ────────────────────────────────────────────────────────────────
if (!strategy.twitter_strategy || !strategy.linkedin_strategy) {
  throw new Error('Strategy missing required twitter_strategy or linkedin_strategy');
}

console.log(`📊 Twitter threads planned: ${strategy.twitter_strategy.thread_count}`);
console.log(`📊 LinkedIn posts planned: ${strategy.linkedin_strategy.post_count}`);

// ────────────────────────────────────────────────────────────────
// STEP 4: MERGE STRATEGY WITH ORIGINAL CONTEXT DATA
// ────────────────────────────────────────────────────────────────
const masterData = {
  // Parsed and cleaned strategy
  content_strategy: strategy,

  // Original context data from Code – Context Merger
  personalContext: contextData.personalContext,
  sourceContent: contextData.sourceContent,
  research: contextData.research,
  workflowMetadata: contextData.workflowMetadata
};

console.log('✅ Strategy + Context merged successfully');
console.log('📦 Master data keys:', Object.keys(masterData));

// ────────────────────────────────────────────────────────────────
// STEP 5: VALIDATE MERGED DATA FOR DOWNSTREAM NODES
// ────────────────────────────────────────────────────────────────
const requiredKeys = ['content_strategy', 'personalContext', 'sourceContent', 'research'];
const missingKeys = requiredKeys.filter(key => !masterData[key]);

if (missingKeys.length > 0) {
  throw new Error(`Merged data missing required keys: ${missingKeys.join(', ')}`);
}

console.log('✅ All required data present for content generation');

return [{ json: masterData }];

// ═══════════════════════════════════════════════════════════════
// UNIFIED: PARSE GEMINI TWITTER + FORMAT AS MARKDOWN + VALIDATE
// FIXED: Handles nested Gemini response structure properly
// ═══════════════════════════════════════════════════════════════

console.log('🐦 Processing and formatting Twitter content...');

try {
  // ────────────────────────────────────────────────────────────────
  // STEP 1: SAFELY EXTRACT RAW TEXT FROM GEMINI'S NESTED STRUCTURE
  // ────────────────────────────────────────────────────────────────
  const geminiRawOutput = $input.first().json;
  let rawText = '';

  // Try multiple levels of nesting
  if (geminiRawOutput?.content?.parts?.[0]?.text) {
    rawText = geminiRawOutput.content.parts[0].text;
    console.log('✅ Extracted from Gemini nested structure: content.parts[0].text');
  } else if (geminiRawOutput?.parts?.[0]?.text) {
    rawText = geminiRawOutput.parts[0].text;
    console.log('✅ Extracted from parts[0].text');
  } else if (typeof geminiRawOutput === 'string') {
    rawText = geminiRawOutput;
    console.log('✅ Raw output is string');
  } else {
    throw new Error(`Cannot extract text. Keys: ${Object.keys(geminiRawOutput).join(', ')}`);
  }

  console.log(`📝 Raw text length: ${rawText.length} characters`);

  // ────────────────────────────────────────────────────────────────
  // STEP 2: EXTRACT JSON FROM MARKDOWN CODE BLOCK (ROBUST)
  // ────────────────────────────────────────────────────────────────
  let twitterData = null;

  // Method 1: Extract from `````` code block
  const jsonCodeBlockMatch = rawText.match(/``````/);
  if (jsonCodeBlockMatch && jsonCodeBlockMatch[1]) {
    try {
      twitterData = JSON.parse(jsonCodeBlockMatch[1].trim());
      console.log('✅ Parsed JSON from ``````');
    } catch (e) {
      console.warn('⚠️ Code block found but JSON parse failed, trying fallback...');
    }
  }

  // Method 2: Direct JSON parsing (if no code block found or failed)
  if (!twitterData) {
    try {
      // Remove any leading/trailing markdown if present
      const cleanJson = rawText.replace(/``````\s*/g, '').trim();
      twitterData = JSON.parse(cleanJson);
      console.log('✅ Parsed JSON directly after removing markdown markers');
    } catch (e) {
      console.warn('⚠️ Direct parse failed too, trying to extract JSON object...');
    }
  }

  // Method 3: Extract JSON object directly with regex
  if (!twitterData) {
    try {
      const jsonMatch = rawText.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        twitterData = JSON.parse(jsonMatch[0]);
        console.log('✅ Extracted and parsed JSON object via regex');
      }
    } catch (e) {
      console.warn('⚠️ Regex extraction also failed');
    }
  }

  // If all methods fail, throw error
  if (!twitterData) {
    throw new Error('Could not parse JSON from any method. Raw text: ' + rawText.substring(0, 200));
  }

  // ────────────────────────────────────────────────────────────────
  // STEP 3: VALIDATE STRUCTURE
  // ────────────────────────────────────────────────────────────────
  if (!twitterData.twitter_threads || !Array.isArray(twitterData.twitter_threads)) {
    throw new Error(`Invalid structure. Expected twitter_threads array. Got: ${Object.keys(twitterData).join(', ')}`);
  }

  console.log(`📊 Found ${twitterData.twitter_threads.length} threads`);

  // ────────────────────────────────────────────────────────────────
  // STEP 4: CLEAN & VALIDATE TWEETS
  // ────────────────────────────────────────────────────────────────
  twitterData.twitter_threads.forEach((thread, threadIdx) => {
    if (!Array.isArray(thread.tweets)) {
      throw new Error(`Thread ${thread.thread_id} has no tweets array`);
    }

    console.log(`  Thread ${thread.thread_id}: ${thread.tweets.length} tweets`);

    thread.tweets.forEach((tweet, tweetIdx) => {
      // Validate content exists
      if (!tweet.content) {
        throw new Error(`Thread ${thread.thread_id}, Tweet ${tweet.position}: Missing content`);
      }

      // Remove unwanted hashtags
      tweet.content = tweet.content.replace(/#AmanSuryavanshi\s*/gi, '').trim();

      // Enforce character limits
      const maxChars = tweet.position <= 3 ? 270 : 245;
      if (tweet.content.length > maxChars) {
        console.warn(`  ⚠️ Thread ${thread.thread_id}, Tweet ${tweet.position}: ${tweet.content.length} chars (max ${maxChars}), trimming...`);
        tweet.content = tweet.content.substring(0, maxChars - 3) + '...';
      }

      // Update char count
      tweet.char_count = tweet.content.length;
    });
  });

  // ────────────────────────────────────────────────────────────────
  // STEP 5: CONVERT TO MARKDOWN FORMAT
  // ────────────────────────────────────────────────────────────────
  let markdownContent = `# Twitter Content Drafts\n\n`;
  markdownContent += `Generated: ${new Date().toLocaleString('en-IN', {timeZone: 'Asia/Kolkata'})}\n`;
  markdownContent += `Total Threads: ${twitterData.twitter_threads.length}\n`;
  markdownContent += `Total Tweets: ${twitterData.twitter_threads.reduce((sum, t) => sum + t.tweets.length, 0)}\n\n`;
  markdownContent += `---\n\n`;

  twitterData.twitter_threads.forEach(thread => {
    markdownContent += `## Thread ${thread.thread_id}\n\n`;
    markdownContent += `**Insight:** ${thread.core_insight}\n\n`;
    markdownContent += `**Audience:** ${thread.target_audience}\n\n`;

    thread.tweets.forEach(tweet => {
      markdownContent += `### Tweet ${tweet.position}/${thread.tweets.length}\n`;
      markdownContent += `**Characters:** ${tweet.char_count}/270\n\n`;
      markdownContent += `${tweet.content}\n\n`;
    });

    markdownContent += `---\n\n`;
  });

  // ────────────────────────────────────────────────────────────────
  // STEP 6: RETURN SUCCESS
  // ────────────────────────────────────────────────────────────────
  const result = {
    markdownContent: markdownContent,
    fileName: `Twitter-Draft-${Date.now()}.md`,
    totalThreads: twitterData.twitter_threads.length,
    totalTweets: twitterData.twitter_threads.reduce((sum, t) => sum + t.tweets.length, 0),
    validationPassed: true,
    timestamp: new Date().toISOString()
  };

  console.log(`✅ SUCCESS: ${result.totalThreads} threads, ${result.totalTweets} tweets`);

  return [{ json: result }];

} catch (error) {
  console.error('❌ ERROR:', error.message);

  return [{
    json: {
      error: true,
      errorMessage: error.message,
      debug: {
        inputKeys: Object.keys($input.first().json || {}),
        hasContent: !!$input.first().json?.content,
        hasContentParts: !!$input.first().json?.content?.parts
      },
      timestamp: new Date().toISOString()
    }
  }];
}

