    // Character count validation
    if (post.content.length < 1800) {
      console.warn(`  ⚠️ Post ${post.post_id}: Too short (${post.content.length} chars, minimum 1800)`);
    }
    if (post.content.length > 2400) {
      console.warn(`  ⚠️ Post ${post.post_id}: Too long (${post.content.length} chars), trimming to 2400...`);
      post.content = post.content.substring(0, 2397) + '...';
    }

    post.char_count = post.content.length;
  });

  // ────────────────────────────────────────────────────────────────
  // STEP 5: CONVERT TO MARKDOWN
  // ────────────────────────────────────────────────────────────────
  let markdownContent = `# LinkedIn Content Drafts\n\n`;
  markdownContent += `Generated: ${new Date().toLocaleString('en-IN', {timeZone: 'Asia/Kolkata'})}\n`;
  markdownContent += `Total Posts: ${linkedinData.linkedin_posts.length}\n\n`;
  markdownContent += `---\n\n`;

  linkedinData.linkedin_posts.forEach(post => {
    markdownContent += `## Post ${post.post_id}\n\n`;
    markdownContent += `**Insight:** ${post.business_insight}\n\n`;
    markdownContent += `**Audience:** ${post.target_audience}\n`;
    markdownContent += `**Characters:** ${post.char_count}/2400\n\n`;
    markdownContent += `${post.content}\n\n`;

    if (Array.isArray(post.hashtags) && post.hashtags.length > 0) {
      markdownContent += `**Hashtags:** ${post.hashtags.join(' ')}\n\n`;
    }

    markdownContent += `---\n\n`;
  });

  // ────────────────────────────────────────────────────────────────
  // STEP 6: RETURN SUCCESS
  // ────────────────────────────────────────────────────────────────
  const result = {
    markdownContent: markdownContent,
    fileName: `LinkedIn-Draft-${Date.now()}.md`,
    totalPosts: linkedinData.linkedin_posts.length,
    validationPassed: true,
    timestamp: new Date().toISOString()
  };

  console.log(`✅ SUCCESS: ${result.totalPosts} posts`);

  return [{ json: result }];

} catch (error) {
  console.error('❌ ERROR:', error.message);

  return [{
    json: {
      error: true,
      errorMessage: error.message,
      debug: {
        inputKeys: Object.keys($input.first().json || {}),
        hasContent: !!$input.first().json?.content,
        hasContentParts: !!$input.first().json?.content?.parts
      },
      timestamp: new Date().toISOString()
    }
  }];
}

