    case 'heading_1':
    case 'heading_2': 
    case 'heading_3':
      if (block[block.type]?.rich_text) {
        const heading = block[block.type].rich_text.map(t => t.plain_text || '').join('');
        const level = '#'.repeat(parseInt(block.type.split('_')[1]));
        return `\n${indent}${level} ${heading}\n`;
      }
      break;

    case 'bulleted_list_item':
    case 'numbered_list_item':
      if (block[block.type]?.rich_text) {
        content = block[block.type].rich_text.map(t => t.plain_text || '').join('');
        let result = `${indent}â€¢ ${content}\n`;

        // Add children if exist
        if (childrenMap[block.id]) {
          childrenMap[block.id].forEach(child => {
            result += processBlock(child, depth + 1);
          });
        }
        return result;
      }
      break;

    case 'toggle':
      if (block.toggle?.rich_text) {
        const toggleTitle = block.toggle.rich_text.map(t => t.plain_text || '').join('');
        let result = `\n${indent}â–¼ ${toggleTitle}\n`;

        // Process toggle children
        if (childrenMap[block.id]) {
          childrenMap[block.id].forEach(child => {
            result += processBlock(child, depth + 1);
          });
        }
        return result;
