        content = block.callout.rich_text.map(t => t.plain_text || '').join('');
        const emoji = block.callout.icon?.emoji || 'ğŸ’¡';
        return `${indent}${emoji} ${content}\n\n`;
      }
      break;

    case 'code':
      if (block.code?.rich_text) {
        content = block.code.rich_text.map(t => t.plain_text || '').join('');
        const lang = block.code.language || 'text';
        return `${indent}\`\`\`${lang}\n${content}\n\`\`\`\n\n`;
