      break;

    case 'image':
      const imageUrl = block.image?.file?.url || block.image?.external?.url;
      const caption = block.image?.caption?.map(c => c.plain_text || '').join('') || '';
      if (imageUrl) {
        return `${indent}[IMAGE: ${caption || 'Visual content'}]\n\n`;
