// Enhanced thread management with rate limiting and proper reply chaining
const content = $input.first().json;
const results = [];

for (let i = 0; i < content.thread.length; i++) {
  const tweet = content.thread[i];
  const isFirstTweet = i === 0;
  const delay = i * 3000; // 3 second delay between tweets for free account

  const tweetData = {
    tweet_text: tweet.tweet_text,
    character_count: tweet.character_count,
    is_thread: true,
    thread_position: i + 1,
    total_tweets: content.thread.length,
    reply_to_tweet_id: isFirstTweet ? null : 'PREVIOUS_TWEET_ID', // Will be updated during posting
    delay_ms: delay,
    originalId: content.originalId,
    hashtags: tweet.hashtags,
    engagement_hook: tweet.engagement_hook,
    isFirstTweet: isFirstTweet,
    isLastTweet: i === content.thread.length - 1
  };

  results.push({
    json: tweetData
  });
}

return results;

// Enhanced campaign analytics with learning focus and detailed metrics
const results = $input.all();
const successCount = results.filter(r => r.json.success !== false).length;
const totalCount = results.length;

const summary = {
  campaign_completed: true,
  total_tweets: totalCount,
  successful_posts: successCount,
  failed_posts: totalCount - successCount,
  completion_rate: `${Math.round((successCount/totalCount) * 100)}%`,
  completed_at: new Date().toISOString(),
  campaign_type: results[0]?.json?.is_thread ? 'thread' : 'single',
  performance_metrics: {
    engagement_potential: 'high',
    learning_focus: 'strong',
    authenticity: 'genuine',
    content_quality: 'educational',
    audience_targeting: 'learners and builders'
  },
  expected_benefits: [
    'Increased engagement from fellow learners',
    'Enhanced reputation as curious learner',
    'Stronger community connections',
    'Higher content reach through authenticity',
    'Improved learning network growth'
  ]
};

return [{
  json: {
    ...summary,
    message: `🎉 Learning Content Posted! Successfully shared ${successCount}/${totalCount} tweets with authentic learning insights.`,
    next_steps: [
      'Monitor engagement and respond to comments',
      'Share follow-up learnings based on responses',
      'Connect with people who engage with your content',
      'Document what resonates most with your audience',
      'Plan next learning content based on community interest'
    ],
    optimization_tips: [
      'Best posting times for learning content: 8-10 AM and 6-8 PM',
      'Engage authentically within first hour',
      'Share follow-up insights based on comments',
      'Build genuine connections with fellow learners',
      'Use learning threads to establish thought leadership'
    ],
    learning_focus_benefits: [
      'More authentic and relatable content',
      'Attracts genuinely interested audience',
      'Creates opportunities for mutual learning',
      'Builds trust through vulnerability',
      'Encourages knowledge sharing community'
    ]
  }
}];

const items = $input.all();
const processedItems = [];

for (const item of items) {
  try {
    const data = item.json;
    const extractText = (richText) => {
      return Array.isArray(richText)
        ? richText.map(i => i.plain_text || '').join(' ').trim()
        : '';
    };

    const content = {
      id: data.id,
      title: extractText(data.properties.About?.title) || 'Learning Journey',
      contentType: data.properties.ContentType?.select?.name || 'thread',
      projectPhase: data.properties.ProjectPhase?.select?.name || 'development',
      status: data.properties.Status?.select?.name || 'Ready to Post',
      category: data.properties.Category?.select?.name || 'Learning',
      tags: data.properties.Tags?.multi_select?.map(tag => tag.name) || [],
      fullPageContent: '',
      hasImages: false,
      images: [],
      extractedSections: {}
    };

    const postingStrategy = content.contentType === 'thread' ? 'thread' : 'single';
    const threadCount = postingStrategy === 'thread'
      ? (content.category === 'tutorial' ? 8
         : content.category === 'case-study' ? 6
         : 5)
      : 1;

    const enhancedPrompt = postingStrategy === 'thread'
      ? `ROLE: You are a curious learner who loves sharing knowledge and building in public. You're passionate about learning and helping others discover valuable insights.

MISSION: Transform the provided content into a ${threadCount}-tweet thread that shows your learning journey and shares valuable insights in an engaging, humble way.

CONTENT CONTEXT:
- Title: ${content.title}
- Category: ${content.category}
- Learning Phase: ${content.projectPhase}
- Tags: ${content.tags.join(', ')}

THREAD STRATEGY:
🎯 Hook (Tweet 1): Start with "I just learned something fascinating about..." or "Been diving deep into..."
📚 Value Delivery (Tweets 2-${threadCount-1}): Share key learnings, insights, and practical takeaways
🚀 Call-to-Action (Tweet ${threadCount}): End with genuine questions to spark discussion

QUALITY REQUIREMENTS:
✅ Each tweet must be under 240 characters (Twitter free account limit)
✅ Use curious language: "discovered", "learned", "fascinating", "turns out"
✅ Include specific examples, numbers, or insights when possible
✅ Add strategic emojis (max 2 per tweet)
✅ Create natural conversation flow between tweets
✅ Include relevant hashtags: #BuildInPublic #Learning #${content.category}
✅ End with genuine questions to encourage engagement

TONE: Curious, humble, enthusiastic about learning, genuinely helpful, conversational

OUTPUT FORMAT (JSON):
{
  "thread": [
    {"tweet_text": "1/${threadCount} 🧵 I just discovered something fascinating about [topic]. Here's what I learned...", "engagement_hook": "curiosity-driven", "cta_type": "learning-share"},
    {"tweet_text": "2/${threadCount} The key insight that blew my mind: [specific learning]...", "engagement_hook": "insight", "cta_type": "value"}
  ],
  "hashtags": ["#BuildInPublic", "#Learning", "#${content.category}"],
  "thread_summary": "Learning journey about ${content.title}",
  "engagement_strategy": "Curious learner sharing valuable insights",
  "target_audience": "Fellow learners and curious minds",
  "viral_potential": "high"
}

CONTENT TO ANALYZE:
[WILL BE FILLED FROM PAGE BLOCKS]`
      : `ROLE: You are a passionate learner who loves sharing discoveries and building in public.

MISSION: Create an engaging single tweet that shares a valuable learning or insight in a humble, curious way.

CONTENT CONTEXT:
- Title: ${content.title}
- Category: ${content.category}
- Learning Phase: ${content.projectPhase}
- Tags: ${content.tags.join(', ')}

TWEET STRATEGY:
🎯 Hook: Start with learning-focused opener ("Just learned...", "Discovered...")
💡 Value: Share one key insight or takeaway
🚀 CTA: End with genuine question to spark discussion

QUALITY REQUIREMENTS:
✅ Maximum 240 characters (Twitter free account)
✅ Include specific examples or insights when possible
✅ Use learning language: "discovered", "learned", "turns out"
✅ Add 1-2 strategic emojis
✅ Include relevant hashtags: #BuildInPublic #Learning #${content.category}
✅ Create genuine curiosity
✅ End with engaging question

TONE: Curious learner, humble, enthusiastic, genuinely helpful

OUTPUT FORMAT (JSON):
{
  "tweet_text": "🤯 Just learned something mind-blowing about [topic]. Turns out [insight]. What's been your biggest learning recently?",
  "hashtags": ["#BuildInPublic", "#Learning", "#${content.category}"],
  "engagement_strategy": "Curious learner sharing insights",
  "call_to_action": "What's your experience with this?",
  "target_audience": "Fellow learners and builders",
  "viral_potential": "high",
  "authority_building": "Knowledge sharing through learning"
}

CONTENT TO ANALYZE:
[WILL BE FILLED FROM PAGE BLOCKS]`;

    processedItems.push({
      json: {
        ...content,
        enhancedPrompt,
        postingStrategy,
        threadCount,
        processingTimestamp: new Date().toISOString()
      }
    });
  } catch (error) {
    console.error('Error processing item:', error);
    processedItems.push({
      json: {
        id: 'fallback',
        title: 'Learning Journey',
        postingStrategy: 'single',
        hasImages: false,
        images: [],
        enhancedPrompt: 'Create a tweet about a learning discovery that shows curiosity and helps others.',
        category: 'learning',
        fallbackMode: true
      }
    });
  }
}

return processedItems;

// Enhanced content parser with robust error handling and learning focus
const items = $input.all();
const results = [];

for (const item of items) {
  try {
    // Parse AI response
    const aiResponse = item.json.candidates?.[0]?.content?.parts?.[0]?.text;

    if (!aiResponse) {
      throw new Error('No AI response received');
    }

    // Extract JSON from response
    let parsedContent;
    try {
      // Try to find JSON in the response
      const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        parsedContent = JSON.parse(jsonMatch[0]);
      } else {
        throw new Error('No JSON found in response');
      }
    } catch (parseError) {
      // Fallback: create structured content from text with learning focus
      const contentType = item.json.extractedContent?.contentType || item.json.contentType || 'thread';
      const title = item.json.extractedContent?.title || item.json.title || 'Learning Discovery';

      if (contentType === 'thread') {
        // Create fallback thread with learning tone
        const sentences = aiResponse.split(/[.!?]+/).filter(s => s.trim().length > 0);
        const tweets = sentences.slice(0, 5).map((sentence, index) => ({
          tweet_text: `${index + 1}/${Math.min(5, sentences.length)} ${sentence.trim().substring(0, 200)}`,
          character_count: Math.min(sentence.trim().length + 10, 240),
          hashtags: ['#BuildInPublic', '#Learning']
        }));

        parsedContent = {
          contentType: 'thread',
          totalTweets: tweets.length,
          thread: tweets,
          thread_summary: `Learning journey about ${title}`,
          target_audience: 'Fellow learners and curious minds',
          viral_potential: 'high',
          authority_building: 'Knowledge sharing through learning',
          engagement_strategy: 'Curious learner sharing valuable insights'
        };
      } else {
        // Create fallback single tweet with learning focus
        const tweetText = `🤯 Just discovered something fascinating about ${title}! ${aiResponse.substring(0, 150).trim()}... What's your experience with this?`;
        parsedContent = {
          contentType: 'single',
          tweet_text: tweetText.substring(0, 240),
          character_count: Math.min(tweetText.length, 240),
          hashtags: ['#BuildInPublic', '#Learning'],
          engagement_strategy: 'Curious learner sharing discovery',
          call_to_action: "What's your experience with this?",
          target_audience: 'Fellow learners and builders',
          viral_potential: 'high',
          authority_building: 'Knowledge sharing through learning',
          value_proposition: 'Learning insight sharing'
        };
      }
    }

    // Validate and enhance parsed content
    if (parsedContent.contentType === 'thread') {
      // Ensure thread structure
      if (!parsedContent.thread || !Array.isArray(parsedContent.thread)) {
        throw new Error('Invalid thread structure');
      }

      // Validate each tweet and ensure 240 char limit
      parsedContent.thread = parsedContent.thread.map((tweet, index) => {
        let tweetText = tweet.tweet_text || `Tweet ${index + 1}`;
        if (tweetText.length > 240) {
          tweetText = tweetText.substring(0, 237) + '...';
        }
        return {
          tweet_text: tweetText,
          character_count: tweetText.length,
          hashtags: tweet.hashtags || ['#BuildInPublic', '#Learning'],
          engagement_hook: tweet.engagement_hook || 'Learning insight',
          has_image: false
        };
      });

      parsedContent.totalTweets = parsedContent.thread.length;
    } else {
      // Ensure single tweet structure and 240 char limit
      let tweetText = parsedContent.tweet_text || 'Sharing a learning discovery!';
      if (tweetText.length > 240) {
        tweetText = tweetText.substring(0, 237) + '...';
      }
      parsedContent.tweet_text = tweetText;
      parsedContent.character_count = tweetText.length;
      parsedContent.hashtags = parsedContent.hashtags || ['#BuildInPublic', '#Learning'];
      parsedContent.has_image = false;
    }

    // Add metadata
    parsedContent.originalId = item.json.originalId || item.json.id;
    parsedContent.generatedAt = new Date().toISOString();
    parsedContent.quality_score = item.json.optimizationScore || 0.8;
    parsedContent.hasImages = item.json.hasImages || false;
    parsedContent.images = item.json.images || [];

    results.push({
      json: parsedContent
    });

  } catch (error) {
    // Ultimate fallback with learning focus
    const title = item.json.extractedContent?.title || item.json.title || 'Learning Journey';
    const fallbackContent = {
      contentType: 'single',
      tweet_text: `🚀 Been exploring ${title} and discovered some fascinating insights! The learning journey never stops. What are you curious about today? #BuildInPublic #Learning`,
      character_count: 140,
      hashtags: ['#BuildInPublic', '#Learning'],
      engagement_strategy: 'Curious learner sharing journey',
      call_to_action: 'What are you learning?',
      target_audience: 'Fellow learners and builders',
      viral_potential: 'medium',
      authority_building: 'Learning-focused knowledge sharing',
      value_proposition: 'Learning journey sharing',
      originalId: item.json.originalId || item.json.id,
      generatedAt: new Date().toISOString(),
      quality_score: 0.6,
      fallback_used: true,
      error_message: error.message,
      hasImages: false,
      images: []
    };

    results.push({
      json: fallbackContent
    });
  }
}

return results;

// Enhanced Visual Preview Generator with learning focus and approval mechanism
const content = $('⚡ Learning Content Parser').first().json;
let previewContent = '';
let visualPreview = '';

// Create detailed visual preview with proper string concatenation
if (content.contentType === 'thread') {
  previewContent = '🧵 LEARNING THREAD PREVIEW\n';
  previewContent += `📊 ${content.totalTweets} tweets | 🎯 ${content.target_audience} | 🚀 ${content.viral_potential} potential\n\n`;

  visualPreview = '📱 TWITTER THREAD VISUALIZATION:\n';
  visualPreview += '┌─────────────────────────────────────┐\n';

  content.thread.forEach((tweet, index) => {
    const tweetNum = `${index + 1}/${content.totalTweets}`;
    const charCount = tweet.character_count || tweet.tweet_text.length;
    const charStatus = charCount <= 240 ? '✅' : '⚠️';

    previewContent += `${tweetNum}: ${tweet.tweet_text}`;
    if (tweet.has_image) {
      previewContent += ' [📷 IMAGE]';
    }
    previewContent += ` ${charStatus} ${charCount}/240\n\n`;

    // Visual representation
    visualPreview += `│ ${tweetNum} ${charStatus} (${charCount}/240)\n`;
    visualPreview += `│ ${tweet.tweet_text.substring(0, 35)}...\n`;
    if (index < content.totalTweets - 1) {
      visualPreview += '│     ↓ REPLY THREAD\n';
    }
  });

  visualPreview += '└─────────────────────────────────────┘\n';

  previewContent += `\n📈 ENGAGEMENT STRATEGY: ${content.engagement_strategy}\n`;
  previewContent += `🏷️ HASHTAGS: ${content.hashtags.join(' ')}\n`;
  previewContent += `📝 SUMMARY: ${content.thread_summary}\n`;

} else {
  const charCount = content.character_count || content.tweet_text.length;
  const charStatus = charCount <= 240 ? '✅' : '⚠️';

  previewContent = '📱 LEARNING TWEET PREVIEW\n';
  previewContent += `🎯 ${content.target_audience} | 🚀 ${content.viral_potential} potential\n\n`;

  visualPreview = '📱 TWITTER POST VISUALIZATION:\n';
  visualPreview += '┌─────────────────────────────────────┐\n';
  visualPreview += `│ ${charStatus} ${charCount}/240 characters\n`;
  visualPreview += `│ ${content.tweet_text}\n`;
  if (content.has_image) {
    visualPreview += '│ [📷 IMAGE ATTACHED]\n';
  }
  visualPreview += '└─────────────────────────────────────┘\n';

  previewContent += `${content.tweet_text}`;
  if (content.has_image) {
    previewContent += ' [📷 IMAGE]';
  }
  previewContent += ` ${charStatus} ${charCount}/240\n\n`;

  previewContent += `📈 STRATEGY: ${content.engagement_strategy}\n`;
  previewContent += `🏷️ HASHTAGS: ${content.hashtags.join(' ')}\n`;
  previewContent += `💬 CTA: ${content.call_to_action}\n`;
  previewContent += `🎯 LEARNING FOCUS: ${content.authority_building}\n`;
}

// Add image information if available
if (content.hasImages && content.images.length > 0) {
  previewContent += `\n📷 IMAGES AVAILABLE (${content.images.length}):\n`;
  content.images.forEach((img, i) => {
    previewContent += `${i+1}. ${img.caption || 'Untitled'} (${img.context || 'General context'})\n`;
  });
  previewContent += '\n⚠️ NOTE: Images cannot be auto-posted to Twitter via this workflow. You\'ll need to manually add them when posting.\n';
}

previewContent += `\n${visualPreview}`;
previewContent += '\n⚡ APPROVAL REQUIRED\n';
previewContent += '\n🎯 TO APPROVE AND POST:\n';
previewContent += '1. Review the content above\n';
previewContent += '2. In the next node (✅ Approval Gate), set the field "userDecision" to "approve"\n';
previewContent += '3. Or set it to "decline" to skip posting\n';
previewContent += '4. Then re-run the workflow from the Approval Gate node\n';
previewContent += `\n🕐 Generated: ${new Date().toLocaleString()}`;

// Add approval instructions
const approvalInstructions = {
  step1: 'Review the content preview above',
  step2: 'Go to the "✅ Approval Gate" node',
  step3: 'Click on the node settings',
  step4: 'Find the condition that checks "userDecision"',
  step5: 'Change the right value from "approve" to your decision',
  step6: 'Set to "approve" to post, "decline" to skip',
  step7: 'Save and re-run from the Approval Gate node'
};

return [{
  json: {
    ...content,
    previewContent,
    visualPreview,
    awaitingApproval: true,
    approvalOptions: ['approve', 'decline'],
    approvalInstructions,
    generatedTimestamp: Date.now(),
    readyForReview: true,
    // Default to decline for safety
    userDecision: 'decline'
  }
}];

const pageData = $input.all();
const originalContent = $('🧠 Learning-Focused Strategy').first().json;
const mainBlocks = $('📄 Extract Page Content').all().map(item => item.json);
const toggleChildren = $('🔄 Get Toggle Children').all().map(item => item.json);

// Merge toggle children with main blocks
const mergedBlocks = [...mainBlocks];
const childrenMap = {};

// Group children by parent toggle
toggleChildren.forEach(childData => {
  if (childData.results) {
    const parentId = childData.parent_id || 'unknown';
    childrenMap[parentId] = childData.results;
  }
});

// Add children to their parent toggles
mergedBlocks.forEach(block => {
  if (block.type === 'toggle' && childrenMap[block.id]) {
    block.toggle.children = childrenMap[block.id];
  }
});

const blocks = mergedBlocks;

// Enhanced content extraction with proper nested handling
const extractPageContent = (blocks, depth = 0) => {
  let fullText = '';
  let images = [];
  let sections = {
    headings: [],
    paragraphs: [],
    bulletPoints: [],
    codeBlocks: [],
    callouts: [],
    keyInsights: [],
    metrics: [],
    toggles: []
  };

  if (!Array.isArray(blocks)) return { fullText, images, sections };

  const processBlock = (block) => {
    const indent = '  '.repeat(depth);

    switch (block.type) {
      case 'paragraph':
        if (block.paragraph?.rich_text) {
          const text = block.paragraph.rich_text.map(t => t.plain_text || '').join('');
          if (text.trim()) {
            fullText += `${indent}${text}\n\n`;
            sections.paragraphs.push(text);

            // Extract metrics
            const metricsMatch = text.match(/\d+[%$]?|\d+\.\d+[%$]?/g);
            if (metricsMatch) sections.metrics.push(...metricsMatch);
          }
        }
        break;

      case 'heading_1':
      case 'heading_2':
      case 'heading_3':
        if (block[block.type]?.rich_text) {
          const heading = block[block.type].rich_text.map(t => t.plain_text || '').join('');
          const level = block.type.split('_')[1];
          const headingPrefix = '#'.repeat(parseInt(level));
          fullText += `\n${indent}${headingPrefix} ${heading}\n`;
          sections.headings.push(heading);
        }
        break;

      case 'bulleted_list_item':
      case 'numbered_list_item':
        const listType = block.type;
        if (block[listType]?.rich_text) {
          const bullet = block[listType].rich_text.map(t => t.plain_text || '').join('');
          fullText += `${indent}• ${bullet}\n`;
          sections.bulletPoints.push(bullet);
        }
        break;

      case 'code':
        if (block.code?.rich_text) {
          const code = block.code.rich_text.map(t => t.plain_text || '').join('');
          const language = block.code.language || 'text';
          fullText += `${indent}\`\`\`${language}\n${code}\n\`\`\`\n\n`;
          sections.codeBlocks.push({ code, language });
        }
        break;

      case 'callout':
        if (block.callout?.rich_text) {
          const callout = block.callout.rich_text.map(t => t.plain_text || '').join('');
          const emoji = block.callout.icon?.emoji || '💡';
          fullText += `${indent}${emoji} ${callout}\n\n`;
          sections.callouts.push(callout);
          sections.keyInsights.push(callout);
        }
        break;

      case 'image':
        const imageUrl = block.image?.file?.url || block.image?.external?.url;
        const caption = block.image?.caption?.map(c => c.plain_text || '').join('') || '';
        if (imageUrl) {
          images.push({
            id: `image_${block.id}`,
            url: imageUrl,
            caption: caption,
            type: block.image.file ? 'file' : 'external',
            context: sections.headings[sections.headings.length - 1] || 'General'
          });
          fullText += `${indent}[IMAGE: ${caption || 'Visual content'}]\n\n`;
        }
        break;

      case 'toggle':
        if (block.toggle?.rich_text) {
          const toggleTitle = block.toggle.rich_text.map(t => t.plain_text || '').join('');
          fullText += `\n${indent}▼ ${toggleTitle}\n`;
          sections.headings.push(toggleTitle);
          sections.toggles.push(toggleTitle);
        }
        break;

      case 'quote':
        if (block.quote?.rich_text) {
          const quote = block.quote.rich_text.map(t => t.plain_text || '').join('');
          fullText += `${indent}> ${quote}\n\n`;
          sections.keyInsights.push(quote);
        }
        break;

      case 'divider':
        fullText += `${indent}\n---\n\n`;
        break;
    }
  };

  blocks.forEach(processBlock);
  return { fullText, images, sections };
};

const { fullText, images, sections } = extractPageContent(blocks);

// Enhanced content analysis
const contentAnalysis = {
  wordCount: fullText.split(/\s+/).filter(word => word.length > 0).length,
  hasHeadings: sections.headings.length > 0,
  hasBulletPoints: sections.bulletPoints.length > 0,
  hasCodeBlocks: sections.codeBlocks.length > 0,
  hasImages: images.length > 0,
  hasCallouts: sections.callouts.length > 0,
  hasMetrics: sections.metrics.length > 0,
  hasToggles: sections.toggles.length > 0,
  keyInsights: sections.keyInsights,
  metrics: sections.metrics,
  estimatedReadTime: Math.ceil(fullText.split(/\s+/).length / 200),
  complexity: 'basic',
  contentValue: 'high'
};

if (sections.codeBlocks.length > 2 || sections.headings.length > 5 || sections.metrics.length > 3) {
  contentAnalysis.complexity = 'advanced';
  contentAnalysis.contentValue = 'very-high';
} else if (sections.codeBlocks.length > 0 || sections.headings.length > 2 || sections.metrics.length > 0) {
  contentAnalysis.complexity = 'intermediate';
  contentAnalysis.contentValue = 'high';
}

const imageDescriptions = images.length > 0 ?
  `\nIMAGES FOUND (${images.length}): \n` +
  images.map((img, i) => `${i+1}. ${img.caption || 'Untitled'} (Context: ${img.context})`).join('\n')
  : '';

const contentSummary = `
CONTENT SUMMARY:
- Word Count: ${contentAnalysis.wordCount}
- Complexity: ${contentAnalysis.complexity}
- Key Insights: ${sections.keyInsights.slice(0, 3).join('; ')}
- Metrics Found: ${sections.metrics.slice(0, 5).join(', ')}
- Main Topics: ${sections.headings.slice(0, 3).join(', ')}
- Toggle Sections: ${sections.toggles.slice(0, 3).join(', ')}${imageDescriptions}

FULL CONTENT:
${fullText}`;

const updatedPrompt = originalContent?.enhancedPrompt
  ? originalContent.enhancedPrompt.replace(
      '[WILL BE FILLED FROM PAGE BLOCKS]',
      contentSummary
    )
  : contentSummary;

return [{
  json: {
    ...originalContent,
    fullPageContent: fullText,
    contentSummary: contentSummary,
    images: images,
    hasImages: images.length > 0,
    imageCount: images.length,
    extractedSections: sections,
    enhancedPrompt: updatedPrompt,
    contentAnalysis: contentAnalysis,
    processingTimestamp: new Date().toISOString(),
    blockCount: blocks.length,
    rawBlocks: blocks
  }
}];

const allBlocks = $input.all();
const processedBlocks = [];

for (const blockItem of allBlocks) {
  const block = blockItem.json;

  // Check if block has children (especially toggles)
  if (block.has_children && (block.type === 'toggle' || block.type === 'bulleted_list_item' || block.type === 'numbered_list_item')) {
    // Mark this block as needing children fetched
    block._needsChildren = true;
    block._blockId = block.id;
  }

  processedBlocks.push({ json: block });
}

return processedBlocks;

const allItems = $input.all();
const mainBlocks = allItems.filter(item => !item.json.results);
const childBlocks = allItems.filter(item => item.json.results);

// Create map of block children
const childrenMap = {};
childBlocks.forEach(child => {
  if (child.json.results) {
    const blockId = child.json.parent_block_id || child.json.parent?.block_id;
    if (blockId) {
      childrenMap[blockId] = child.json.results;
    }
  }
});

// Process and enhance blocks with their children
const processBlock = (block, depth = 0) => {
  let content = '';
  const indent = '  '.repeat(depth);

  switch (block.type) {
    case 'paragraph':
      if (block.paragraph?.rich_text) {
        content = block.paragraph.rich_text.map(t => t.plain_text || '').join('').trim();
        if (content) return `${indent}${content}\n\n`;
      }
      break;

    case 'heading_1':
    case 'heading_2': 
    case 'heading_3':
      if (block[block.type]?.rich_text) {
        const heading = block[block.type].rich_text.map(t => t.plain_text || '').join('');
        const level = '#'.repeat(parseInt(block.type.split('_')[1]));
        return `\n${indent}${level} ${heading}\n`;
      }
      break;

    case 'bulleted_list_item':
    case 'numbered_list_item':
      if (block[block.type]?.rich_text) {
        content = block[block.type].rich_text.map(t => t.plain_text || '').join('');
        let result = `${indent}• ${content}\n`;

        // Add children if exist
        if (childrenMap[block.id]) {
          childrenMap[block.id].forEach(child => {
            result += processBlock(child, depth + 1);
          });
        }
        return result;
      }
      break;

    case 'toggle':
      if (block.toggle?.rich_text) {
        const toggleTitle = block.toggle.rich_text.map(t => t.plain_text || '').join('');
        let result = `\n${indent}▼ ${toggleTitle}\n`;

        // Process toggle children
        if (childrenMap[block.id]) {
          childrenMap[block.id].forEach(child => {
            result += processBlock(child, depth + 1);
          });
        }
        return result;
      }
      break;

    case 'callout':
      if (block.callout?.rich_text) {
        content = block.callout.rich_text.map(t => t.plain_text || '').join('');
        const emoji = block.callout.icon?.emoji || '💡';
        return `${indent}${emoji} ${content}\n\n`;
      }
      break;

    case 'code':
      if (block.code?.rich_text) {
        content = block.code.rich_text.map(t => t.plain_text || '').join('');
        const lang = block.code.language || 'text';
        return `${indent}\`\`\`${lang}\n${content}\n\`\`\`\n\n`;
      }
      break;

    case 'image':
      const imageUrl = block.image?.file?.url || block.image?.external?.url;
      const caption = block.image?.caption?.map(c => c.plain_text || '').join('') || '';
      if (imageUrl) {
        return `${indent}[IMAGE: ${caption || 'Visual content'}]\n\n`;
      }
      break;

    case 'quote':
      if (block.quote?.rich_text) {
        content = block.quote.rich_text.map(t => t.plain_text || '').join('');
        return `${indent}> ${content}\n\n`;
      }
      break;
  }

  return '';
};

// Process all main blocks
let fullContent = '';
const extractedData = {
  headings: [],
  paragraphs: [],
  toggles: [],
  bulletPoints: [],
  keyInsights: [],
  metrics: []
};

mainBlocks.forEach(item => {
  const block = item.json;
  const processedContent = processBlock(block);
  fullContent += processedContent;

  // Extract metadata
  if (block.type === 'toggle' && block.toggle?.rich_text) {
    extractedData.toggles.push(block.toggle.rich_text.map(t => t.plain_text).join(''));
  }
  if (block.type.includes('heading') && block[block.type]?.rich_text) {
    extractedData.headings.push(block[block.type].rich_text.map(t => t.plain_text).join(''));
  }
  if (block.type === 'paragraph' && block.paragraph?.rich_text) {
    const text = block.paragraph.rich_text.map(t => t.plain_text).join('');
    extractedData.paragraphs.push(text);
    // Extract metrics
    const metrics = text.match(/\d+[%$]?|\d+\.\d+[%$]?/g);
    if (metrics) extractedData.metrics.push(...metrics);
  }
});

const originalContent = $('🧠 Learning-Focused Strategy').first().json;

const contentSummary = `
CONTENT SUMMARY:
- Word Count: ${fullContent.split(/\s+/).length}
- Main Topics: ${extractedData.headings.slice(0, 3).join(', ')}
- Toggle Sections: ${extractedData.toggles.slice(0, 3).join(', ')}
- Key Metrics: ${extractedData.metrics.slice(0, 5).join(', ')}

FULL CONTENT:
${fullContent}`;

const updatedPrompt = originalContent?.enhancedPrompt?.replace(
  '[WILL BE FILLED FROM PAGE BLOCKS]',
  contentSummary
) || contentSummary;

return [{
  json: {
    ...originalContent,
    fullPageContent: fullContent,
    contentSummary: contentSummary,
    extractedSections: extractedData,
    enhancedPrompt: updatedPrompt,
    processingTimestamp: new Date().toISOString()
  }
}];
